from collections import Counter 
def parse(col):
    bigtxt = ";".join(df[col].dropna())
    wrds = bigtxt.split(";")
    wrds = Counter(wrds).most_common()
    resp = {'name' : col, "children" : [], "size":""}
    for wrd in wrds:
        doc = {'name' : wrd[0], "size": wrd[1]}
        resp['children'].append(doc)
    return resp
